// Minimal PDF.js worker for local development
// This is a simplified worker that provides basic PDF processing functionality
// without external dependencies that might be blocked by CORS

self.addEventListener('message', function(e) {
  // Basic worker functionality - this is a placeholder
  // The actual PDF.js will handle the heavy lifting
  self.postMessage({
    type: 'ready',
    data: 'PDF worker initialized'
  });
});

// PDF.js worker compatibility
if (typeof self !== 'undefined') {
  self.pdfWorkerReady = true;
}